create table logIn(id integer GENERATED BY DEFAULT AS IDENTITY primary key,userName varchar(20) not null,pwd varchar(20) not null,email varchar(74),lvl integer default 16)
create table text(id integer GENERATED BY DEFAULT AS IDENTITY primary key,userName integer not null,text long varchar not null,foreign key(userName) references logIn(id) on delete cascade)
create table icon(id integer GENERATED BY DEFAULT AS IDENTITY primary key,userName integer not null,"path" varchar(64) not null,foreign key(userName) references logIn(id) on delete cascade)
create view afo(name,text) as select l.userName,t.text from logIn l,text t where t.userName=l.id
create table img(id integer GENERATED BY DEFAULT AS IDENTITY primary key,a varchar(64),b varchar(64),c varchar(64),d varchar(64),e varchar(64),f varchar(64),g varchar(64),h varchar(64),i varchar(64),j varchar(64),k varchar(64),l varchar(64),m varchar(64))
